-- =====================================================
-- Dialogue content table + seed data
-- =====================================================

CREATE TABLE IF NOT EXISTS public.dialogues (
  id UUID PRIMARY KEY DEFAULT uuid_generate_v4(),
  level_id INTEGER NOT NULL REFERENCES public.levels(id) ON DELETE CASCADE,
  display_order INTEGER NOT NULL,
  speaker TEXT NOT NULL,
  speaker_thai TEXT,
  emoji TEXT,
  thai TEXT NOT NULL,
  roman TEXT,
  english TEXT,
  correct TEXT,
  options JSONB NOT NULL DEFAULT '[]'::jsonb,
  created_at TIMESTAMPTZ DEFAULT NOW()
);

CREATE UNIQUE INDEX IF NOT EXISTS idx_dialogues_level_order ON public.dialogues(level_id, display_order);

ALTER TABLE public.dialogues ENABLE ROW LEVEL SECURITY;
DROP POLICY IF EXISTS "Anyone can view dialogues" ON public.dialogues;
CREATE POLICY "Anyone can view dialogues" ON public.dialogues FOR SELECT USING (true);


ON CONFLICT (level_id, display_order) DO UPDATE SET
  speaker = EXCLUDED.speaker,
  speaker_thai = EXCLUDED.speaker_thai,
  emoji = EXCLUDED.emoji,
  thai = EXCLUDED.thai,
  roman = EXCLUDED.roman,
  english = EXCLUDED.english,
  correct = EXCLUDED.correct,
  options = EXCLUDED.options;
